(function () {
    setScrollOnClick('a.about-nav-link', '#about-link');
    setScrollOnClick('a.controls-nav-link', '#controls-link');
    setScrollOnClick('a.screenshots-nav-link', '#screenshots-link');
    setScrollOnClick('a.play-nav-link', '#play-link');

    function setScrollOnClick(cleckedElement, scrollToElement) {
        $(cleckedElement).bind('click', function() {
            $('html,body').animate({
                scrollTop: $(scrollToElement).offset().top-120
            }, 1500);
        });
    }
})();